insert into zapp_date_t (dt_s, dt_e) select sd as dt_s , sdl as dt_e from (
select date_add(#{date.stasdate, jdbcType=VARCHAR}, interval -WEEKDAY(#{date.stasdate, jdbcType=VARCHAR})-1 day) as sd , date_add(date_add(#{date.stasdate, jdbcType=VARCHAR}, interval -WEEKDAY(#{date.stasdate, jdbcType=VARCHAR})-1 day), interval 6 day) as sdl
) LIST
